mapper.select:
SELECT  *
FROM    cart
WHERE   member_id = #{memberId}
AND     item_id = #{itemId}

mapper.update:
UPDATE  cart
SET     count = #{count},
        cart_price = #{cartPrice}
WHERE   cart_id = #{cartId}
AND     member_id = #{memberId}
AND     item_id = #{itemId}

mapper.delete:
DELETE
FROM    cart
WHERE   cart_id = #{cartId}
AND     member_id = #{memberId}
AND     item_id = #{itemId}
